<script>
import "scroll-behavior-polyfill";

export default {
  name: "ScrollDownCta",
  props: ["scrollCtaHeight", "quizStarted"],
  mounted() {
    // Will handle initial scroll down to first question when starting quiz
    this.quizStarted &&
      setTimeout(() => {
        window.scrollTo({
          top:
            this.$refs.scrollCta.offsetHeight + this.$refs.scrollCta.offsetTop,
          behavior: "smooth"
        });
      }, 700);
  }
};
</script>

